UPDATE creature_template
SET ScriptName = CONCAT(ScriptName, '_multi')
WHERE ScriptName IN('npc_prof_alchemy', 'npc_prof_blacksmith', 'npc_engineering_tele_trinket', 'npc_prof_leather', 'npc_prof_tailor');

UPDATE quest_template_addon
SET ExclusiveGroup = 0
WHERE ExclusiveGroup IN(3526,5301,10831,10905,5283,3638,10897,5141,5145);


UPDATE quest_template
SET RewardSpell = 0
WHERE RewardSpell IN(9789,9790,10657,10659,10661);



